import React, { useState, useEffect } from 'react';
import axios from 'axios';
import OrderDetails from './'

const CustomerOrders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedOrder, setSelectedOrder] = useState(null); // New state to hold the selected order

  useEffect(() => {
    const fetchOrders = async () => {
      setLoading(true);
      try {
        const response = await axios.get('http://localhost:5001/api/orders');
        setOrders(response.data);
      } catch (error) {
        console.error('Error fetching orders:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);

  const formatDate = (dateString) => {
    const options = {
      weekday: 'long', year: 'numeric', month: 'long', day: 'numeric',
      hour: 'numeric', minute: 'numeric', second: 'numeric', hour12: true
    };
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', options);
  };

  // Handle selecting an order to view details
  const handleViewDetails = (order) => {
    setSelectedOrder(order);
  };

  return (
    <div>
      {/* Render OrderDetails if an order is selected */}
      {selectedOrder ? (
        <OrderDetails order={selectedOrder} />
      ) : (
        <div>
          <center><h1>Customer Orders</h1></center>
          <center><h2>Your Orders</h2></center>
          <center>
            <table>
              <thead>
                <tr>
                  <th>Customer Name</th>
                  <th>Product</th>
                  <th>Quantity</th>
                  <th>NIC</th>
                  <th>Account Number</th>
                  <th>Phone Number</th>
                  <th>Address</th>
                  <th>Email</th>
                  <th>Amount</th>
                  <th>Status</th>
                  <th>Created At</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {loading ? (
                  <tr><td colSpan="12">Loading...</td></tr>
                ) : (
                  orders.map((order) => (
                    <tr key={order._id}>
                      <td>{order.customerName}</td>
                      <td>{order.product}</td>
                      <td>{order.quantity}</td>
                      <td>{order.NIC}</td>
                      <td>{order.AccountNumber}</td>
                      <td>{order.PhoneNumber}</td>
                      <td>{order.Address}</td>
                      <td>{order.Email}</td>
                      <td>{order.Amount}</td>
                      <td>{order.status}</td>
                      <td>{formatDate(order.createdAt)}</td>
                      <td>
                        <button onClick={() => handleViewDetails(order)} id="viewDetailsBtn">View Details</button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </center>
        </div>
      )}
    </div>
  );
};

export default CustomerOrders;
