import React from 'react';
//import './UserOrderdetails.css'; // Import the CSS file

const formatDate = (dateString) => {
  const options = {
    weekday: 'long', year: 'numeric', month: 'long', day: 'numeric',
    hour: 'numeric', minute: 'numeric', second: 'numeric', hour12: true
  };
  const date = new Date(dateString);
  return date.toLocaleDateString('en-US', options);
};

const getStatusColor = (status) => {
  switch (status) {
    case 'Shipped':
      return 'blue';
    case 'Completed':
      return 'green';
    case 'Canceled':
      return 'red';
    default:
      return 'gray';
  }
};

const UserOrderdetails = ({ orders=[], loading, handleEditOrder, handleCancelOrder }) => {
    
  return (
    <center>
      <table>
        <thead>
          <tr>
            <th>Customer Name</th>
            <th>Product</th>
            <th>Image</th>
            <th>Quantity</th>
            <th>NIC</th>
            <th>Account Number</th>
            <th>Phone Number</th>
            <th>Address</th>
            <th>Email</th>
            <th>Amount</th>
            
            <th>Created At</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {loading ? (
            <tr><td colSpan="12">Loading...</td></tr>
          ) : (
            orders.map((order) => (
              <tr key={order._id}>
                <td>{order.customerName}</td>
                <td>{order.product}</td>
                <td>{order.image}</td>
                <td>{order.quantity}</td>
                <td>{order.NIC}</td>
                <td>{order.AccountNumber}</td>
                <td>{order.PhoneNumber}</td>
                <td>{order.Address}</td>
                <td>{order.Email}</td>
                <td>{order.Amount}</td>
                
                <td style={{ color: getStatusColor(order.status) }}>{order.status}</td>
               
                <td>
                  <button onClick={() => handleEditOrder(order)} id="editbtn">Edit</button><br />
                  <button onClick={() => handleCancelOrder(order._id)} id="cancelbtn">Cancel</button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </center>
  );
};

export default UserOrderdetails;
