import React, { useState, useEffect } from 'react';

import axios from 'axios';

const CustomerOrders = () => {
  const [orders, setOrders] = useState([]);
  const [customerName, setCustomerName] = useState('');
  const [product, setProduct] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [NIC, setNic] = useState('');
  const [AccountNumber, setAccountNumber] = useState('');
  const [PhoneNumber, setPhoneNumber] = useState('');
  const [Address, setAddress] = useState('');
  const [Email, setEmail] = useState('');
  const [Amount, setAmount] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [editOrderId, setEditOrderId] = useState(null);
  const [loading, setLoading] = useState(false);
  const[file,setFile]=useState('');
  const [image,setImage]= useState('');


  useEffect(() => {

    const fetchOrders = async () => {
      setLoading(true);
      try {
        const response = await axios.get('http://localhost:5001/api/orders');
        setOrders(response.data);
      } catch (error) {
        console.error('Error fetching orders:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);

  useEffect(()=>{
    axios.get('http://localhost:5001/api/getImage')
    .then(res => console.log(res))
    .catch(err => console.log(err))    
  })

  // Helper function to format the date
  const formatDate = (dateString) => {
    const options = {
      weekday: 'long', year: 'numeric', month: 'long', day: 'numeric',
      hour: 'numeric', minute: 'numeric', second: 'numeric', hour12: true
    };
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', options);
  };

  const handleCreateOrder = async () => {
    if (!customerName || !product || !NIC || !AccountNumber || !PhoneNumber || !Address || !Email || !Amount) {
      alert('Please fill in all the fields.');
      return;
    }
  
    if (Address.trim().length < 10) {
      alert('Address must be at least 10 characters long.');
      return;
    }
  
    try {
      const response = await axios.post('http://localhost:5001/api/orders', {
        customerName,
        product,
  
        quantity,
        NIC,
        AccountNumber,
        PhoneNumber,
        Address,
        Email,
        Amount,
      });
  
      setOrders((prevOrders) => [...prevOrders, response.data]);
      resetForm();
      alert('Order created successfully!');
    } catch (error) {
      console.error('Error creating order:', error.response ? error.response.data : error.message);
      alert(`Error: ${error.response ? error.response.data.message : 'Failed to create order'}`);
    }
  };

  const handleUpload = (e) =>{
    const formdata = new FormData()
    formdata.append('file',file)
    axios.post('http://localhost:5001/api/upload',formdata)
    .then(res => setImage(res.data[0].image))
    .catch(err => console.log(err))
  }
  
  
  const handleEditOrder = (order) => {
    if (order.status !== 'Pending') {
      alert('You can only edit orders with "Pending" status.');
      return;
    }

    setIsEditing(true);
    setEditOrderId(order._id);
    setCustomerName(order.customerName);
    setProduct(order.product);
    setQuantity(order.quantity);
    setNic(order.NIC);
    setAccountNumber(order.AccountNumber);
    setPhoneNumber(order.PhoneNumber);
    setAddress(order.Address);
    setEmail(order.Email);
    setAmount(order.Amount);
  };

  const handleUpdateOrder = async () => {
    if (!customerName || !product || !NIC || !AccountNumber || !PhoneNumber || !Address || !Email || !Amount) {
      alert('Please fill in all the fields.');
      return;
    }
  
    if (Address.trim().length < 10) {
      alert('Address must be at least 10 characters long.');
      return;
    }
  
    const orderData = {
      customerName,
      product,
      quantity,
      NIC,
      AccountNumber,
      PhoneNumber,
      Address,
      Email,
      Amount,
      status: 'Pending', // You can set the status based on your requirements
    };
  
    setLoading(true);  // Show loading spinner
  
    try {
      const response = await axios.put(`http://localhost:5001/api/orders/${editOrderId}`, orderData);
      setOrders((prevOrders) =>
        prevOrders.map((order) => (order._id === editOrderId ? response.data : order))
      );
      resetForm();
      alert('Order updated successfully!');
    } catch (error) {
      console.error('Error updating order:', error);
      alert('Failed to update the order');
    } finally {
      setLoading(false);  // Hide loading spinner
    }
  };
  
  const handleCancelOrder = async (orderId) => {
    try {
      const response = await axios.delete(`http://localhost:5001/api/orders/${orderId}`);
      console.log('Order canceled:', response.data);
  
      // Re-fetch the list of orders after canceling
      const updatedOrders = await axios.get('http://localhost:5001/api/orders');
      setOrders(updatedOrders.data);  // Update the state with the new orders list
    } catch (error) {
      console.error('Error canceling order:', error.response.data);
    }
  };
  
  
  
  
  const resetForm = () => {
    setIsEditing(false);
    setEditOrderId(null);
    setCustomerName('');
    setProduct('');
   
    setQuantity(1);
    setNic('');
    setAccountNumber('');
    setPhoneNumber('');
    setAddress('');
    setEmail('');
    setAmount('');
  };

  // Dynamic text color for status column based on the current status
  const getStatusColor = (status) => {
    switch (status) {
      case 'Shipped':
        return 'blue'; // Blue text for shipped orders
      case 'Completed':
        return 'green'; // Green text for completed orders
      case 'Canceled':
        return 'red'; // Red text for canceled orders
      default:
        return 'gray'; // Default gray text for pending orders
    }
  };

  return (
    <div>
      <ul>
        <li><a className="active" href="#home">Home</a></li>
        <li><a href="#news">News</a></li>
        <li><a href="#contact">Contact</a></li>
        <li><a href="#about">About</a></li>
        <input type="text" placeholder="Search.." name="search" className="search" />
        <button type="submit"><i className="fa fa-search"></i></button>
      </ul>

      <center><h1>Customer Ordersconst</h1></center>

      {/* Form for creating or editing orders */}
      {isEditing ? (
        <div className="div">
          <h2>Edit Order</h2>
          <form onSubmit={(e) => { e.preventDefault(); handleUpdateOrder(); }} className="editform">
            {/* Form Inputs */}
            <label>Customer Name:</label><br />
            <input type="text" value={customerName} onChange={(e) => setCustomerName(e.target.value)} placeholder="Your Name" /><br />
            <label>Product:</label><br />
            <input type="text" value={product} onChange={(e) => setProduct(e.target.value)} placeholder="Product" /><br />
            <label>Quantity:</label><br />
            <input type="number" value={quantity} onChange={(e) => setQuantity(Number(e.target.value))} /><br />
            <label>NIC:</label><br />
            <input type="text" value={NIC} onChange={(e) => setNic(e.target.value)} placeholder="Enter the NIC" /><br />
            <label>Account Number:</label><br />
            <input type="text" value={AccountNumber} onChange={(e) => setAccountNumber(e.target.value)} placeholder="Enter the Account Number" /><br />
            <label>Phone Number:</label><br />
            <input type="text" value={PhoneNumber} onChange={(e) => setPhoneNumber(e.target.value)} placeholder="Enter the Phone Number" /><br />
            <label>Address:</label><br />
            <input type="text" value={Address} onChange={(e) => setAddress(e.target.value)} placeholder="Enter the Address" /><br />
            <label>Email:</label><br />
            <input type="text" value={Email} onChange={(e) => setEmail(e.target.value)} placeholder="Enter the Email" /><br />
            <label>Amount:</label><br />
            <input type="text" value={Amount} onChange={(e) => setAmount(e.target.value)} placeholder="Enter the amount" /><br />
            <button type="submit" id="updatebtn">Update Order</button><br />
            <button type="button" onClick={() => setIsEditing(false)} id="cancelEditbtn">Cancel</button>
          </form>
        </div>
      ) : (
        <div className="div">
          <form onSubmit={(e) => { e.preventDefault(); handleCreateOrder(); }} className="editform">
            {/* Form Inputs */}
            <label>Customer Name:</label><br />
            <input type="text" value={customerName} onChange={(e) => setCustomerName(e.target.value)} placeholder="Your Name" /><br />
            <label>Product:</label><br />
            <input type="text" value={product} onChange={(e) => setProduct(e.target.value)} placeholder="Product" /><br />
            <input multiple onChange={(e)=> setFile(e.target.files[0])} type="file"/>
            <button onClick={handleUpload}>Upload</button><br/>
            <img src={`http://localhost:500`} alt=""/>
            <label>Quantity:</label><br />
            <input type="number" value={quantity} onChange={(e) => setQuantity(Number(e.target.value))} /><br />
            <label>NIC:</label><br />
            <input type="text" value={NIC} onChange={(e) => setNic(e.target.value)} placeholder="Enter the NIC" /><br />
            <label>Account Number:</label><br />
            <input type="text" value={AccountNumber} onChange={(e) => setAccountNumber(e.target.value)} placeholder="Enter the Account Number" /><br />
            <label>Phone Number:</label><br />
            <input type="text" value={PhoneNumber} onChange={(e) => setPhoneNumber(e.target.value)} placeholder="Enter the Phone Number" /><br />
            <label>Address:</label><br />
            <input type="text" value={Address} onChange={(e) => setAddress(e.target.value)} placeholder="Enter the Address" /><br />
            <label>Email:</label><br />
            <input type="text" value={Email} onChange={(e) => setEmail(e.target.value)} placeholder="Enter the Email" /><br />
            <label>Amount:</label><br />
            <input type="text" value={Amount} onChange={(e) => setAmount(e.target.value)} placeholder="Enter the amount" /><br />
            <button type="submit" id="createbtn">Create Order</button>
          </form>
        </div>
      )}

      <center><h2>Your Orders</h2></center>
      <center>
        <table>
          <thead>
            <tr>
              <th>Customer Name</th>
              <th>Product</th>
              <th>Quantity</th>
              <th>NIC</th>
              <th>Account Number</th>
              <th>Phone Number</th>
              <th>Address</th>
              <th>Email</th>
              <th>Amount</th>
              <th>Status</th>
              <th>Created At</th> {/* New Column for Date/Time */}
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr><td colSpan="12">Loading...</td></tr>
            ) : (
              orders.map((order) => (
                <tr key={order._id}>
                  <td>{order.customerName}</td>
                  <td>{order.product}</td>
                  <td>{order.quantity}</td>
                  <td>{order.NIC}</td>
                  <td>{order.AccountNumber}</td>
                  <td>{order.PhoneNumber}</td>
                  <td>{order.Address}</td>
                  <td>{order.Email}</td>
                  <td>{order.Amount}</td>
                  <td style={{ color: getStatusColor(order.status) }}>{order.status}</td>
                  <td>{formatDate(order.createdAt)}</td> {/* Displaying the created date */}
                  <td>
                    <button onClick={() => handleEditOrder(order)} id="editbtn">Edit</button><br/>
                    <button onClick={() => handleCancelOrder(order._id)} id="cancelbtn">Cancel</button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </center>
    </div>
  );
};

export default CustomerOrders;
