import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [showDetails, setShowDetails] = useState(false); // State for toggling the visibility of email and password
  const [passwordVisible, setPasswordVisible] = useState(false); // State for toggling password visibility
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch user data on dashboard load
    const fetchUserData = async () => {
      const token = localStorage.getItem('authToken');
      if (token) {
        const decodedToken = JSON.parse(atob(token.split('.')[1]));
        try {
          const response = await axios.get(`http://localhost:5001/api/users/${decodedToken.userId}`);
          setUser(response.data);
        } catch (error) {
          console.error('Error fetching user data:', error);
        }
      } else {
        navigate('/login'); // Redirect to login if not authenticated
      }
    };

    fetchUserData();
  }, [navigate]);

  // Logout functionality
  const handleLogout = () => {
    localStorage.removeItem('authToken');
    navigate('/login');
  };

  // Toggle visibility of password
  const togglePasswordVisibility = () => {
    setPasswordVisible(!passwordVisible);
  };

  // Toggle visibility of email and password
  const toggleDetailsVisibility = () => {
    setShowDetails(!showDetails);
  };

  return (
    <div className="dashboard">
      <center>
        <h1>Welcome to Your Dashboard</h1>
      </center>

      {user ? (
        <div>
          <div className="user-info">
            <h2>User Information</h2>
            <p><strong>Name:</strong> {user.name}</p>
            <p><strong>Phone:</strong> {user.phone}</p>
            <p><strong>Address:</strong> {user.address}</p>
            
            {/* Button to toggle visibility of email and password */}
            <button onClick={toggleDetailsVisibility}>
              {showDetails ? 'Hide Details' : 'View Details'}
            </button>
            
            {/* Conditional rendering for email and password */}
            {showDetails && (
              <div>
                <p><strong>Email:</strong> {user.email}</p>
                <p>
                  <strong>Password:</strong> 
                  {passwordVisible ? user.password : '******'}
                  <button onClick={togglePasswordVisibility}>
                    {passwordVisible ? 'Hide Password' : 'Show Password'}
                  </button>
                </p>
              </div>
            )}
          </div>

          <div className="profile-actions">
            <button onClick={() => navigate('/edit-profile')}>Edit Profile</button>
            <button onClick={handleLogout}>Log Out</button>
          </div>
        </div>
      ) : (
        <div>Loading user data...</div>
      )}
    </div>
  );
};

export default Dashboard;
