import React, { useState } from "react";
import axios from "axios";

const Cart = () => {
  const [cart, setCart] = useState([]);
// eslint-disable-next-line no-unused-vars
const [userId, setUserId] = useState();
  const [qty, setQty] = useState(1);
  const [buttonColor, setButtonColor] = useState("initial");

  const addToCart = async (productId) => {
    try {
      // Send data to the server to update the cart
      await axios.post("/api/cart", { productId, userId, qty });

      // Update the button color to green
      setButtonColor("green");

      // Optionally update the local cart state
      setCart((prevCart) => [
        ...prevCart,
        { productId, userId, qty }
      ]);
    } catch (error) {
      console.error("Error adding to cart", error);
    }
  };

  return (
    <div>
      <button
        onClick={() => addToCart("product123")} // Replace with dynamic productId
        style={{ backgroundColor: buttonColor }}
      >
        Add to Cart
      </button>

      <div>
        <h3>Cart</h3>
        <ul>
          {cart.map((item, index) => (
            <li key={index}>
              Product ID: {item.productId}, Quantity: {item.qty}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Cart;
