import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CustomerOrders = () => {
  const [orders, setOrders] = useState([]);
  const [customerName, setCustomerName] = useState('');
  const [product, setProduct] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [NIC, setNic] = useState('');
  const [AccountNumber, setAccountNumber] = useState('');
  const [PhoneNumber, setPhoneNumber] = useState('');
  const [Address, setAddress] = useState('');
  const [Email, setEmail] = useState('');
  const [Amount, setAmount] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [editOrderId, setEditOrderId] = useState(null);
  const [loading, setLoading] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Authentication state
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('authToken');
    if (token) {
      setIsAuthenticated(true);
      const decodedToken = JSON.parse(atob(token.split('.')[1]));
      setUserId(decodedToken.userId); // Assuming the userId is in the token
      fetchOrders(decodedToken.userId); // Fetch orders for the logged-in user
    } else {
      setIsAuthenticated(false);
    }
  }, []);

  const fetchOrders = async (userId) => {
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:5001/api/orders?userId=${userId}`);
      setOrders(response.data);
    } catch (error) {
      console.error('Error fetching orders:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    const options = {
      weekday: 'long', year: 'numeric', month: 'long', day: 'numeric',
      hour: 'numeric', minute: 'numeric', second: 'numeric', hour12: true
    };
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', options);
  };

  // Handle Edit Order
  const handleEditOrder = (order) => {
    setCustomerName(order.customerName);
    setProduct(order.product);
    setQuantity(order.quantity);
    setNic(order.NIC);
    setAccountNumber(order.AccountNumber);
    setPhoneNumber(order.PhoneNumber);
    setAddress(order.Address);
    setEmail(order.Email);
    setAmount(order.Amount);
    setIsEditing(true);
    setEditOrderId(order._id);
  };

  // Handle Cancel Order
  const handleCancelOrder = async (orderId) => {
    try {
      await axios.delete(`http://localhost:5001/api/orders/${orderId}`);
      fetchOrders(userId); // Refresh the list of orders after cancellation
      alert('Order canceled successfully');
    } catch (error) {
      console.error('Error canceling order:', error);
      alert('Error canceling order');
    }
  };

  // Handle Create Order
  const handleCreateOrder = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5001/api/orders', {
        customerName,
        product,
        quantity,
        NIC,
        AccountNumber,
        PhoneNumber,
        Address,
        Email,
        Amount,
      });
      fetchOrders(userId); // Refresh the orders list after creating a new order
      alert('Order created successfully');
      resetForm();
    } catch (error) {
      console.error('Error creating order:', error);
      alert('Error creating order');
    }
  };

  // Handle Update Order
  const handleUpdateOrder = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:5001/api/orders/${editOrderId}`, {
        customerName,
        product,
        quantity,
        NIC,
        AccountNumber,
        PhoneNumber,
        Address,
        Email,
        Amount,
      });
      fetchOrders(userId); // Refresh the orders list after updating
      alert('Order updated successfully');
      resetForm();
    } catch (error) {
      console.error('Error updating order:', error);
      alert('Error updating order');
    }
  };

  // Reset the form after creating or editing an order
  const resetForm = () => {
    setCustomerName('');
    setProduct('');
    setQuantity(1);
    setNic('');
    setAccountNumber('');
    setPhoneNumber('');
    setAddress('');
    setEmail('');
    setAmount('');
    setIsEditing(false);
    setEditOrderId(null);
  };

  // Render content only if user is authenticated
  if (!isAuthenticated) {
    return (
      <div>
        <center>
          <h1>Please Log In to View Your Orders</h1>
          <button onClick={() => window.location.href = '/login'}>Go to Login Page</button>
        </center>
      </div>
    );
  }

  return (
    <div>
    <ul>
      <li><a className="active" href="#home">Home</a></li>
      <li><a href="#news">News</a></li>
      <li><a href="#contact">Contact</a></li>
      <li><a href="#about">About</a></li>
      <input type="text" placeholder="Search.." name="search" className="search" />
      <button type="submit"><i className="fa fa-search"></i></button>
    </ul>

    <center><h1>Customer Orders</h1></center>

    {/* Form for creating or editing orders */}
    {isEditing && (
      <div className="div">
        <h2>Edit Order</h2>
        <form onSubmit={(e) => { e.preventDefault(); handleUpdateOrder(); }} className="editform">
        <label>Customer Name:</label><br />
          <input type="text" value={customerName} onChange={(e) => setCustomerName(e.target.value)} placeholder="Your Name" /><br />
          <label>Product:</label><br />
          <input type="text" value={product} onChange={(e) => setProduct(e.target.value)} placeholder="Product" /><br />
          <label>Quantity:</label><br />
          <input type="number" value={quantity} onChange={(e) => setQuantity(Number(e.target.value))} /><br />
          <label>NIC:</label><br />
          <input type="text" value={NIC} onChange={(e) => setNic(e.target.value)} placeholder="Enter the NIC" /><br />
          <label>Account Number:</label><br />
          <input type="text" value={AccountNumber} onChange={(e) => setAccountNumber(e.target.value)} placeholder="Enter the Account Number" /><br />
          <label>Phone Number:</label><br />
          <input type="text" value={PhoneNumber} onChange={(e) => setPhoneNumber(e.target.value)} placeholder="Enter the Phone Number" /><br />
          <label>Address:</label><br />
          <input type="text" value={Address} onChange={(e) => setAddress(e.target.value)} placeholder="Enter the Address" /><br />
          <label>Email:</label><br />
          <input type="text" value={Email} onChange={(e) => setEmail(e.target.value)} placeholder="Enter the Email" /><br />
          <label>Amount:</label><br />
          <input type="text" value={Amount} onChange={(e) => setAmount(e.target.value)} placeholder="Enter the amount" /><br />
          <button type="submit" id="updatebtn">Update Order</button><br />
          <button type="button" onClick={() => setIsEditing(false)} id="cancelEditbtn">Cancel</button>
        </form>
      </div>
    )}

    {!isEditing && (
      <div className="div">
        <form onSubmit={(e) => { e.preventDefault(); handleCreateOrder(); }} className="editform">
        <label>Customer Name:</label><br />
          <input type="text" value={customerName} onChange={(e) => setCustomerName(e.target.value)} placeholder="Your Name" /><br />
          <label>Product:</label><br />
          <input type="text" value={product} onChange={(e) => setProduct(e.target.value)} placeholder="Product" /><br />
          <label>Quantity:</label><br />
          <input type="number" value={quantity} onChange={(e) => setQuantity(Number(e.target.value))} /><br />
          <label>NIC:</label><br />
          <input type="text" value={NIC} onChange={(e) => setNic(e.target.value)} placeholder="Enter the NIC" /><br />
          <label>Account Number:</label><br />
          <input type="text" value={AccountNumber} onChange={(e) => setAccountNumber(e.target.value)} placeholder="Enter the Account Number" /><br />
          <label>Phone Number:</label><br />
          <input type="text" value={PhoneNumber} onChange={(e) => setPhoneNumber(e.target.value)} placeholder="Enter the Phone Number" /><br />
          <label>Address:</label><br />
          <input type="text" value={Address} onChange={(e) => setAddress(e.target.value)} placeholder="Enter the Address" /><br />
          <label>Email:</label><br />
          <input type="text" value={Email} onChange={(e) => setEmail(e.target.value)} placeholder="Enter the Email" /><br />
          <label>Amount:</label><br />
          <input type="text" value={Amount} onChange={(e) => setAmount(e.target.value)} placeholder="Enter the amount" /><br />
          <button type="submit" id="createbtn">Create Order</button>
        </form>
      </div>
    )}

    <center><h2>Your Orders</h2></center>
    <center>
      <table>
        <thead>
          <tr>
            <th>Customer Name</th>
            <th>Product</th>
            <th>Quantity</th>
            <th>NIC</th>
            <th>Account Number</th>
            <th>Phone Number</th>
            <th>Address</th>
            <th>Email</th>
            <th>Amount</th>
            <th>Status</th>
            <th>Created At</th> {/* New Column for Date/Time */}
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {loading ? (
            <tr><td colSpan="12">Loading...</td></tr>
          ) : (
            orders.map((order) => (
              <tr key={order._id}>
                <td>{order.customerName}</td>
                <td>{order.product}</td>
                <td>{order.quantity}</td>
                <td>{order.NIC}</td>
                <td>{order.AccountNumber}</td>
                <td>{order.PhoneNumber}</td>
                <td>{order.Address}</td>
                <td>{order.Email}</td>
                <td>{order.Amount}</td>
                <td>{order.status}</td>
                <td>{formatDate(order.createdAt)}</td> {/* Displaying the created date */}
                <td>
                  <button onClick={() => handleEditOrder(order)} id="editbtn">Edit</button>
                  <button onClick={() => handleCancelOrder(order._id)} id="cancelbtn">Cancel</button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </center>
  </div>

  );
};

export default CustomerOrders;
