import React, { useState } from 'react';
import CustomerDetails from './CustomerDetails';  // Ensure correct path

const ProductPage = ({ product, userID, addToCart }) => {
  // Dummy customer data (this could come from an API or props)
  const [customer] = useState({
    name: "John Doe",
    email: "john.doe@example.com",
    phone: "123-456-7890"
  });

  return (
    <div>
      <h2>{product.name}</h2>
      <img src={product.image} alt={product.name} width="150px" />
      <p>Price: ${product.price}</p>
      <button onClick={() => addToCart(product, userID)}>Add to Cart</button>

      {/* Integrating CustomerDetails */}
      <CustomerDetails customer={customer} />
    </div>
  );
};

export default ProductPage;
