import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CustomerOrders = () => {
  const [orders, setOrders] = useState([]);
  const [customerName, setCustomerName] = useState('');
  const [product, setProduct] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [NIC, setNic] = useState('');
  const [AccountNumber, setAccountNumber] = useState('');
  const [PhoneNumber, setPhoneNumber] = useState('');
  const [Address, setAddress] = useState('');
  const [Email, setEmail] = useState('');
  const [Amount, setAmount] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [editOrderId, setEditOrderId] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchOrders = async () => {
      setLoading(true);
      try {
        const response = await axios.get('http://localhost:5001/api/orders');
        setOrders(response.data);
      } catch (error) {
        console.error('Error fetching orders:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchOrders();
  }, []);

  const handleCreateOrder = async () => {
    if (!customerName || !product || !NIC || !AccountNumber || !PhoneNumber || !Address || !Email || !Amount) {
      alert('Please fill in all the fields.');
      return;
    }

    if (Address.trim().length < 10) {
      alert('Address must be at least 10 characters long.');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5001/api/orders', {
        customerName,
        product,
        quantity,
        NIC,
        AccountNumber,
        PhoneNumber,
        Address,
        Email,
        Amount,
      });

      setOrders((prevOrders) => [...prevOrders, response.data]);
      resetForm();
    } catch (error) {
      console.error('Error creating order:', error);
      alert(`Error: ${error.response ? error.response.data.message : 'Failed to create order'}`);
    }
  };

  const handleEditOrder = (order) => {
    if (order.status !== 'Pending') {
      alert('You can only edit orders with "Pending" status.');
      return;
    }

    setIsEditing(true);
    setEditOrderId(order._id);
    setCustomerName(order.customerName);
    setProduct(order.product);
    setQuantity(order.quantity);
    setNic(order.NIC);
    setAccountNumber(order.AccountNumber);
    setPhoneNumber(order.PhoneNumber);
    setAddress(order.Address);
    setEmail(order.Email);
    setAmount(order.Amount);
  };

  const handleUpdateOrder = async () => {
    if (!customerName || !product || !NIC || !AccountNumber || !PhoneNumber || !Address || !Email || !Amount) {
      alert('Please fill in all the fields.');
      return;
    }

    if (Address.trim().length < 10) {
      alert('Address must be at least 10 characters long.');
      return;
    }

    const orderData = {
      customerName,
      product,
      quantity,
      NIC,
      AccountNumber,
      PhoneNumber,
      Address,
      Email,
      Amount,
      status: 'Pending', // Always set status to "Pending"
    };

    try {
      const response = await axios.put(`http://localhost:5001/api/orders/${editOrderId}`, orderData);
      setOrders((prevOrders) =>
        prevOrders.map((order) => (order._id === editOrderId ? response.data : order))
      );
      resetForm();
      alert('Order updated successfully!');
    } catch (error) {
      console.error('Error updating order:', error);
      const errorMessage = error.response ? error.response.data.message : 'An unknown error occurred.';
      alert(`Failed to update the order: ${errorMessage}`);
    }
  };

  const handleCancelOrder = async (id) => {
    try {
      await axios.delete(`http://localhost:5001/api/orders/${id}`);
      setOrders((prevOrders) => prevOrders.filter((order) => order._id !== id));
    } catch (error) {
      console.error('Error cancelling order:', error);
      alert('Failed to cancel order.');
    }
  };

  const resetForm = () => {
    setIsEditing(false);
    setEditOrderId(null);
    setCustomerName('');
    setProduct('');
    setQuantity(1);
    setNic('');
    setAccountNumber('');
    setPhoneNumber('');
    setAddress('');
    setEmail('');
    setAmount('');
  };

  return (
    <div>
      <h1>Customer Orders</h1><br />

      {isEditing && (
        <center>
          <div>
            <h2>Edit Order</h2>
            <form onSubmit={(e) => { e.preventDefault(); handleUpdateOrder(); }}>
              <label>Customer Name:</label><br />
              <input
                type="text"
                value={customerName}
                onChange={(e) => setCustomerName(e.target.value)}
                placeholder="Your Name"
              /><br />
              <label>Product:</label><br />
              <input
                type="text"
                value={product}
                onChange={(e) => setProduct(e.target.value)}
                placeholder="Product"
              /><br />
              <label>Quantity:</label><br />
              <input
                type="number"
                value={quantity}
                onChange={(e) => setQuantity(Number(e.target.value))}
              /><br />
              <label>NIC:</label><br />
              <input
                type="text"
                value={NIC}
                onChange={(e) => setNic(e.target.value)}
                placeholder="Enter the NIC"
              /><br />
              <label>Account Number:</label><br />
              <input
                type="text"
                value={AccountNumber}
                onChange={(e) => setAccountNumber(e.target.value)}
                placeholder="Enter the Account Number"
              /><br />
              <label>Phone Number:</label><br />
              <input
                type="text"
                value={PhoneNumber}
                onChange={(e) => setPhoneNumber(e.target.value)}
                placeholder="Enter the Phone Number"
              /><br />
              <label>Address:</label><br />
              <input
                type="text"
                value={Address}
                onChange={(e) => setAddress(e.target.value)}
                placeholder="Enter the Address"
              /><br />
              <label>Email:</label><br />
              <input
                type="text"
                value={Email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Enter the Email"
              /><br />
              <label>Amount:</label><br />
              <input
                type="text"
                value={Amount}
                onChange={(e) => setAmount(e.target.value)}
                placeholder="Enter the amount"
              /><br />
              <button type="submit" id="updatebtn">Update Order</button>
              <button type="button" onClick={() => setIsEditing(false)} id="cancelEditbtn">Cancel</button>
            </form>
          </div>
        </center>
      )}

      {!isEditing && (
        <div>
          <h2>Create Order</h2>
          <form onSubmit={(e) => { e.preventDefault(); handleCreateOrder(); }}>
            <label>Customer Name:</label><br />
            <input
              type="text"
              value={customerName}
              onChange={(e) => setCustomerName(e.target.value)}
              placeholder="Your Name"
            /><br />
            <label>Product:</label><br />
            <input
              type="text"
              value={product}
              onChange={(e) => setProduct(e.target.value)}
              placeholder="Product"
            /><br />
            <label>Quantity:</label><br />
            <input
              type="number"
              value={quantity}
              onChange={(e) => setQuantity(Number(e.target.value))}
            /><br />
            <label>NIC:</label><br />
            <input
              type="text"
              value={NIC}
              onChange={(e) => setNic(e.target.value)}
              placeholder="Enter the NIC"
            /><br />
            <label>Account Number:</label><br />
            <input
              type="text"
              value={AccountNumber}
              onChange={(e) => setAccountNumber(e.target.value)}
              placeholder="Enter the Account Number"
            /><br />
            <label>Phone Number:</label><br />
            <input
              type="text"
              value={PhoneNumber}
              onChange={(e) => setPhoneNumber(e.target.value)}
              placeholder="Enter the Phone Number"
            /><br />
            <label>Address:</label><br />
            <input
              type="text"
              value={Address}
              onChange={(e) => setAddress(e.target.value)}
              placeholder="Enter the Address"
            /><br />
            <label>Email:</label><br />
            <input
              type="text"
              value={Email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter the Email"
            /><br />
            <label>Amount:</label><br />
            <input
              type="text"
              value={Amount}
              onChange={(e) => setAmount(e.target.value)}
              placeholder="Enter the amount"
            /><br />
            <button type="submit" id="createbtn">Create Order</button>
          </form>
        </div>
      )}

      <center><h2>Your Orders</h2></center>
      <center>
        <table>
          <thead>
            <tr>
              <th>Customer Name</th>
              <th>Product</th>
              <th>Quantity</th>
              <th>NIC</th>
              <th>Account Number</th>
              <th>Phone Number</th>
              <th>Address</th>
              <th>Email</th>
              <th>Amount</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr><td colSpan="11">Loading...</td></tr>
            ) : (
              orders.map((order) => (
                <tr key={order._id}>
                  <td>{order.customerName}</td>
                  <td>{order.product}</td>
                  <td>{order.quantity}</td>
                  <td>{order.NIC}</td>
                  <td>{order.AccountNumber}</td>
                  <td>{order.PhoneNumber}</td>
                  <td>{order.Address}</td>
                  <td>{order.Email}</td>
                  <td>{order.Amount}</td>
                  <td>{order.status}</td>
                  <td>
                    <button onClick={() => handleEditOrder(order)} id="editbtn">Edit</button>
                    <button onClick={() => handleCancelOrder(order._id)} id="cancelbtn">Cancel</button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </center>
    </div>
  );
};

export default CustomerOrders;
