import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [ordersCount, setOrdersCount] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch user data and orders count on dashboard load
    const fetchUserData = async () => {
      const token = localStorage.getItem('authToken');
      if (token) {
        const decodedToken = JSON.parse(atob(token.split('.')[1]));
        try {
          const response = await axios.get(`http://localhost:5001/api/users/${decodedToken.userId}`);
          setUser(response.data);
        } catch (error) {
          console.error('Error fetching user data:', error);
        }
      } else {
        navigate('/login'); // Redirect to login if not authenticated
      }
    };

    const fetchOrdersCount = async () => {
      const token = localStorage.getItem('authToken');
      if (token) {
        const decodedToken = JSON.parse(atob(token.split('.')[1]));
        try {
          const response = await axios.get(`http://localhost:5001/api/orders/count?userId=${decodedToken.userId}`);
          setOrdersCount(response.data.count);
        } catch (error) {
          console.error('Error fetching orders count:', error);
        }
      }
    };

    fetchUserData();
    fetchOrdersCount();
  }, [navigate]);

  // Logout functionality
  const handleLogout = () => {
    localStorage.removeItem('authToken');
    navigate('/login');
  };

  return (
    <div className="dashboard">
      <center>
        <h1>Welcome to Your Dashboard</h1>
      </center>

      {user ? (
        <div>
          <div className="user-info">
            <h2>User Information</h2>
            <p><strong>Name:</strong> {user.name}</p>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Phone:</strong> {user.phone}</p>
            <p><strong>Address:</strong> {user.address}</p>
          </div>

          <div className="orders-info">
            <h2>Orders Information</h2>
            <p><strong>Orders Count:</strong> {ordersCount}</p>
            <button onClick={() => navigate('/customer-orders')}>View Orders</button>
          </div>

          <div className="profile-actions">
            <button onClick={() => navigate('/edit-profile')}>Edit Profile</button>
            <button onClick={handleLogout}>Log Out</button>
          </div>
        </div>
      ) : (
        <div>Loading user data...</div>
      )}
    </div>
  );
};

export default Dashboard;
