import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminOrderTable = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch all orders when the component mounts
  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/admin');
        console.log(response.data); // Log the response for debugging
        setOrders(response.data);
      } catch (error) {
        console.error('Error fetching orders:', error.response ? error.response.data : error.message);
        alert('Failed to load orders.');
      } finally {
        setLoading(false);
      }
    };
    

    fetchOrders();
  }, []);

  // Function to handle canceling an order
  const handleCancelOrder = async (id) => {
    try {
      const response = await axios.delete(`http://localhost:5001/api/admin/${id}`);
      console.log(response.data); // Log the response for debugging
      // Remove canceled order from UI
      setOrders((prevOrders) => prevOrders.filter((order) => order._id !== id));
      alert('Order canceled successfully!');
    } catch (error) {
      console.error('Error canceling order:', error.response ? error.response.data : error.message);
      alert('Failed to cancel the order.');
    }
  };

  // Function to handle updating the status of an order
  const handleUpdateStatus = async (id, status) => {
    try {
      const response = await axios.put(
        `http://localhost:5001/api/admin/${id}`,
        { status }
      );
      const updatedOrder = response.data;

      // Update the status locally in the UI
      setOrders((prevOrders) =>
        prevOrders.map((order) =>
          order._id === id ? { ...order, status: updatedOrder.status } : order
        )
      );
    } catch (error) {
      console.error('Error updating order status:', error.response ? error.response.data : error.message);
      alert('Failed to update the order status.');
    }
  };

  // Display a loading message while fetching data
  if (loading) {
    return <div>Loading orders...</div>;
  }

  // Function to format dates
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return new Intl.DateTimeFormat('en-US', {
      weekday: 'short',
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: 'numeric',
      minute: 'numeric',
      second: 'numeric',
      hour12: true,
    }).format(date);
  };

  // Dynamic text color for status column based on the current status
  const getStatusColor = (status) => {
    switch (status) {
      case 'Shipped':
        return 'blue'; // Blue text for shipped orders
      case 'Completed':
        return 'green'; // Green text for completed orders
      case 'Canceled':
        return 'red'; // Red text for canceled orders
      default:
        return 'gray'; // Default gray text for pending orders
    }
  };

  return (
    <div>
      <ul>
        <li><a className="active" href="#home">Home</a></li>
        <li><a href="#news">News</a></li>
        <li><a href="#contact">Contact</a></li>
        <li><a href="#about">About</a></li>
        <input type="text" placeholder="Search.." name="search" className="search" />
        <button type="submit"><i className="fa fa-search"></i></button>
      </ul>

      <center><h1>Admin Order Management</h1></center>

      <center>
        <table>
          <thead>
            <tr>
              <th>Customer Name</th>
              <th>Product</th>
              <th>Quantity</th>
              <th>NIC</th>
              <th>Account Number</th>
              <th>Amount</th>
              <th>Phone Number</th>
              <th>Address</th>
              <th>Email</th>
              <th>Status</th>
              <th>Created At</th>
              <th>Updated At</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order) => (
              <tr key={order._id}>
                <td>{order.customerName}</td>
                <td>{order.product}</td>
                <td>{order.quantity}</td>
                <td>{order.NIC}</td>
                <td>{order.AccountNumber}</td>
                <td>{order.Amount}</td>
                <td>{order.PhoneNumber}</td>
                <td>{order.Address}</td>
                <td>{order.Email}</td>
                <td style={{ color: getStatusColor(order.status) }}>
                  {order.status}
                </td>
                <td>{formatDate(order.createdAt)}</td>
                <td>{formatDate(order.updatedAt)}</td>
                <td>
                  <button
                    onClick={() => handleCancelOrder(order._id)}
                    disabled={order.status === 'Shipped' || order.status === 'Completed'}
                    className="canclebutton"
                  >
                    Cancel
                  </button>
                  <button
                    onClick={() => handleUpdateStatus(order._id, 'Shipped')}
                    disabled={order.status === 'Shipped' || order.status === 'Completed'}
                    className="shippedbutton"
                  >
                    Shipped
                  </button>
                  <button
                    onClick={() => handleUpdateStatus(order._id, 'Completed')}
                    disabled={order.status === 'Completed'}
                    className="completedbutton"
                  >
                    Completed
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </center>
    </div>
  );
};

export default AdminOrderTable;
