import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CustomerOrders = () => {
  const [orders, setOrders] = useState([]);
  const [customerName, setCustomerName] = useState('');
  const [product, setProduct] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [NIC, setNic] = useState('');
  const [AccountNumber, setAccountNumber] = useState('');
  const [PhoneNumber, setPhoneNumber] = useState('');
  const [Address, setAddress] = useState('');
  const [Email, setEmail] = useState('');
  const [Amount, setAmount] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchOrders = async () => {
      setLoading(true);
      try {
        const response = await axios.get('http://localhost:5001/api/orders');
        setOrders(response.data);
      } catch (error) {
        console.error('Error fetching orders:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchOrders();
  }, []);

  const formatDate = (dateString) => {
    if (!dateString) return "N/A";
    return new Date(dateString).toLocaleDateString('en-US', {
      weekday: 'long', year: 'numeric', month: 'long', day: 'numeric',
      hour: 'numeric', minute: 'numeric', second: 'numeric', hour12: true
    });
  };

  const resetForm = () => {
    setCustomerName('');
    setProduct('');
    setQuantity(1);
    setNic('');
    setAccountNumber('');
    setPhoneNumber('');
    setAddress('');
    setEmail('');
    setAmount('');
  };

  const handleCreateOrder = async () => {
    if (!customerName || !product || !NIC || !AccountNumber || !PhoneNumber || !Address || !Email || !Amount) {
      alert('Please fill in all the fields.');
      return;
    }

    if (Address.trim().length < 10) {
      alert('Address must be at least 10 characters long.');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5001/api/orders', {
        customerName, product, quantity, NIC, AccountNumber, PhoneNumber, Address, Email, Amount,
      });

      setOrders([...orders, response.data]);
      resetForm();
      alert('Order created successfully!');
    } catch (error) {
      console.error('Error creating order:', error);
      alert('Failed to create order.');
    }
  };

  const handleCancelOrder = async (id) => {
    try {
      await axios.delete(`http://localhost:5001/api/orders/${id}`);
      setOrders(orders.filter((order) => order._id !== id));
      alert('Order Cancelled successfully!');
    } catch (error) {
      console.error('Error cancelling order:', error);
      alert('Failed to cancel order.');
    }
  };

  return (
    <div>
      <center><h1>Customer Orders</h1></center>

      {/* Order Creation Form */}
      <div className="div">
        <form onSubmit={(e) => { e.preventDefault(); handleCreateOrder(); }}>
          <label>Customer Name:</label><br />
          <input type="text" value={customerName} onChange={(e) => setCustomerName(e.target.value)} /><br />
          <label>Product:</label><br />
          <input type="text" value={product} onChange={(e) => setProduct(e.target.value)} /><br />
          <label>Quantity:</label><br />
          <input type="number" value={quantity} onChange={(e) => setQuantity(Number(e.target.value))} /><br />
          <label>NIC:</label><br />
          <input type="text" value={NIC} onChange={(e) => setNic(e.target.value)} /><br />
          <label>Account Number:</label><br />
          <input type="text" value={AccountNumber} onChange={(e) => setAccountNumber(e.target.value)} /><br />
          <label>Phone Number:</label><br />
          <input type="text" value={PhoneNumber} onChange={(e) => setPhoneNumber(e.target.value)} /><br />
          <label>Address:</label><br />
          <input type="text" value={Address} onChange={(e) => setAddress(e.target.value)} /><br />
          <label>Email:</label><br />
          <input type="text" value={Email} onChange={(e) => setEmail(e.target.value)} /><br />
          <label>Amount:</label><br />
          <input type="text" value={Amount} onChange={(e) => setAmount(e.target.value)} /><br />
          <button type="submit">Create Order</button>
        </form>
      </div>

      {/* Orders Table */}
      <center><h2>Your Orders</h2></center>
      <center>
        <table>
          <thead>
            <tr>
              <th>Customer Name</th>
              <th>Product</th>
              <th>Quantity</th>
              <th>NIC</th>
              <th>Account Number</th>
              <th>Phone Number</th>
              <th>Address</th>
              <th>Email</th>
              <th>Amount</th>
              <th>Status</th>
              <th>Created At</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr><td colSpan="12">Loading...</td></tr>
            ) : (
              orders.map((order) => (
                <tr key={order._id}>
                  <td>{order.customerName}</td>
                  <td>{order.product}</td>
                  <td>{order.quantity}</td>
                  <td>{order.NIC}</td>
                  <td>{order.AccountNumber}</td>
                  <td>{order.PhoneNumber}</td>
                  <td>{order.Address}</td>
                  <td>{order.Email}</td>
                  <td>{order.Amount}</td>
                  <td>{order.status}</td>
                  <td>{formatDate(order.createdAt)}</td>
                  <td>
                    <button onClick={() => handleCancelOrder(order._id)}>Cancel</button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </center>
    </div>
  );
};

export default CustomerOrders;
