import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminOrderTable = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch all orders when the component mounts
  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await axios.get('http://localhost:5004/api/orders'); // Change this URL
        setOrders(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching orders:', error);
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);

  // Function to handle canceling an order
  const handleCancelOrder = async (id) => {
    try {
      await axios.delete(`http://localhost:5004/api/orders/${id}`); // Correct the endpoint
      setOrders(orders.filter((order) => order._id !== id)); // Remove canceled order from UI
    } catch (error) {
      console.error('Error canceling order:', error);
    }
  };

  // Function to handle updating the status of an order
  const handleUpdateStatus = async (id, status) => {
    try {
      await axios.put(`http://localhost:5004/api/orders/${id}`, { status }); // Correct the endpoint
      // Update the status locally in the UI
      setOrders(
        orders.map((order) =>
          order._id === id ? { ...order, status } : order
        )
      );
    } catch (error) {
      console.error('Error updating order status:', error);
    }
  };

  // Display a loading message while fetching data
  if (loading) {
    return <div>Loading orders...</div>;
  }

  return (
    <div>
      <ul>
  <li><a class="active" href="#home">Home</a></li>
  <li><a href="#news">News</a></li>
  <li><a href="#contact">Contact</a></li>
  <li><a href="#about">About</a></li>
</ul>

     <center> <h1>Admin Order Management</h1></center>
      <table class="center">
        <thead>
          <tr>
            <th>Customer Name</th>
            <th>Product</th>
            <th>Quantity</th>
            <th>NIC</th>
            <th>Address</th>
            <th>PhoneNumber</th>
            <th>Email</th>
            <th>Amount</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {orders.map((order) => (
            <tr key={order._id}>
              <td>{order.customerName}</td>
              <td>{order.product}</td>
              <td>{order.quantity}</td>
              <td>{order.NIC}</td> {/* Fixed the NIC field */}
              <td>{order.Address}</td>
              <td>{order.PhoneNumber}</td>
              <td>{order.Email}</td>
              <td>{order.Amount}</td> {/* Ensure 'amount' exists in your Order schema */}
              <td>{order.status}</td>
              <td>
                <button onClick={() => handleCancelOrder(order._id)} class="canclebtn">
                  Cancel
                </button><br/>
                <button
                  onClick={() =>
                    handleUpdateStatus(order._id, 'Processing')
                  }
                class="processingbtn">
                   Processing
                </button><br/>
                <button
                  onClick={() =>
                    handleUpdateStatus(order._id, 'Shipped')
                  }
                class="shippingbtn">
                   Shipped
                </button><br/>
                <button
                  onClick={() =>
                    handleUpdateStatus(order._id, 'Completed')
                  }
               class="completebtn" >
                  Completed
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminOrderTable;
