import React, { useState } from 'react';
import './App.css';
import HomePage from './components/HomePage';  
import CustomerOrders from './components/CustomerOrders';
import AdminOrderTable from './components/AdminOrderTable';
import ProductPage from './components/ProductPage';
import CustomerDetails from './components/CustomerDetails'; // ✅ Import it
import { createBrowserRouter, RouterProvider } from "react-router-dom";

const router = createBrowserRouter([
  { path: "/", element: <HomePage /> }, 
  { path: "/customerorder", element: <CustomerOrders /> },
  { path: "/adminorder", element: <AdminOrderTable /> },
]);

function App() {
  const [cart, setCart] = useState([]);
  const userID = 123; // Example user ID

  const addToCart = (product, userID) => {
    setCart(prevCart => {
      const existingItem = prevCart.find(item => item.productID === product.id);

      if (existingItem) {
        return prevCart.map(item =>
          item.productID === product.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      } else {
        return [...prevCart, { 
          userID, 
          productID: product.id, 
          name: product.name, 
          image: product.image, 
          quantity: 1 
        }];
      }
    });
  };

  return (
    <RouterProvider router={router}>
      <div>
        <ProductPage 
          product={{ id: 1, name: "Product A", image: "productA.jpg", price: 100 }} 
          userID={userID} 
          addToCart={addToCart} 
        />
        <CustomerDetails cart={cart} setCart={setCart} />
      </div>
    </RouterProvider>
  );
}

export default App;
