import React, { useState, useEffect } from 'react';
import axios from 'axios';
im
const OrdersPage = () => {
  const [orders, setOrders] = useState([]);
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const { data } = await axios.get('http://localhost:5001/api/admin');
        console.log(data);
        setOrders(data);
      } catch (error) {
        const errorMessage = error.response
          ? error.response.data
          : error.message || 'An unknown error occurred';
        console.error('Error fetching orders:', errorMessage);
        setError('Failed to load orders. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);

  const addToCart = (order) => {
    // Check if the item is already in the cart
    const itemExists = cart.some(item => item.id === order.id);
    if (itemExists) {
      alert('Item is already in the cart!');
    } else {
      setCart(prevCart => [...prevCart, order]);
    }
  };

  const removeFromCart = (orderId) => {
    setCart(prevCart => prevCart.filter(item => item.id !== orderId));
  };

  return (
    <div>
      <h1>Orders</h1>

      {loading && <p>Loading orders...</p>}
      {error && <p>{error}</p>}

      <table border="1">
        <thead>
          <tr>
            <th>Order ID</th>
            <th>Product</th>
            <th>Price</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {orders.map(order => (
            <tr key={order.id}>
              <td>{order.id}</td>
              <td>{order.product}</td>
              <td>${order.price}</td>
              <td>
                <button onClick={() => addToCart(order)}>Add to Cart</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>Cart</h2>
      <table border="1">
        <thead>
          <tr>
            <th>Order ID</th>
            <th>Product</th>
            <th>Price</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {cart.map(item => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.product}</td>
              <td>${item.price}</td>
              <td>
                <button onClick={() => removeFromCart(item.id)}>Remove</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default OrdersPage;
