import React, { useState, useEffect } from 'react';
import axios from 'axios';
import OrderDetails from '.pages/OrderDetails';

const customerOrder = () => {
  const [orders, setOrders] = useState([]);
  const [selectedOrderId, setSelectedOrderId] = useState(null); // State to hold selected order ID
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchOrders = async () => {
      setLoading(true);
      try {
        const response = await axios.get('http://localhost:5001/api/orders');
        setOrders(response.data);
      } catch (error) {
        console.error('Error fetching orders:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchOrders();
  }, []);

  const handleViewOrderDetails = (orderId) => {
    setSelectedOrderId(orderId);
  };

  const handleCloseOrderDetails = () => {
    setSelectedOrderId(null); // Reset the selected order to close the details view
  };

  return (
    <div>
      <ul>
        <li><a className="active" href="#home">Home</a></li>
        <li><a href="#news">News</a></li>
        <li><a href="#contact">Contact</a></li>
        <li><a href="#about">About</a></li>
        <input type="text" placeholder="Search.." name="search" className="search" />
        <button type="submit"><i className="fa fa-search"></i></button>
      </ul>

      <center><h1>Customer Orders</h1></center>

      {selectedOrderId && (
        <OrderDetails orderId={selectedOrderId} onClose={handleCloseOrderDetails} />
      )}

      {!selectedOrderId && (
        <div>
          <center><h2>Your Orders</h2></center>
          <center>
            <table>
              <thead>
                <tr>
                  <th>Customer Name</th>
                  <th>Product</th>
                  <th>Quantity</th>
                  <th>NIC</th>
                  <th>Account Number</th>
                  <th>Phone Number</th>
                  <th>Address</th>
                  <th>Email</th>
                  <th>Amount</th>
                  <th>Status</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {loading ? (
                  <tr><td colSpan="11">Loading...</td></tr>
                ) : (
                  orders.map((order) => (
                    <tr key={order._id}>
                      <td>{order.customerName}</td>
                      <td>{order.product}</td>
                      <td>{order.quantity}</td>
                      <td>{order.NIC}</td>
                      <td>{order.AccountNumber}</td>
                      <td>{order.PhoneNumber}</td>
                      <td>{order.Address}</td>
                      <td>{order.Email}</td>
                      <td>{order.Amount}</td>
                      <td>{order.status}</td>
                      <td>
                        <button onClick={() => handleViewOrderDetails(order._id)} id="viewbtn">View Details</button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </center>
        </div>
      )}
    </div>
  );
};

export default customerOrder;
