import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminOrderTable = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');

  // Fetch all orders when the component mounts
  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/admin');
        if (response.data && Array.isArray(response.data)) {
          setOrders(response.data);
        } else {
          alert('No orders found.');
        }
      } catch (error) {
        console.error('Error fetching orders:', error.response ? error.response.data : error.message);
        alert('Failed to load orders.');
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const filteredOrders = orders.filter(order =>
    order.customerName.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Function to format dates
  const formatDate = (dateString) => {
    if (!dateString) return 'Invalid date'; // Fallback for missing date

    const date = new Date(dateString);

    // Check if the date is valid
    if (isNaN(date.getTime())) {
      return 'Invalid date'; // Return fallback for invalid dates
    }

    return new Intl.DateTimeFormat('en-US', {
      weekday: 'short',
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: 'numeric',
      minute: 'numeric',
      second: 'numeric',
      hour12: true,
    }).format(date);
  };

  // Dynamic text color for status column based on the current status
  const getStatusColor = (status) => {
    switch (status) {
      case 'Shipped':
        return 'blue'; // Blue text for shipped orders
      case 'Completed':
        return 'green'; // Green text for completed orders
      case 'Canceled':
        return 'red'; // Red text for canceled orders
      default:
        return 'gray'; // Default gray text for pending orders
    }
  };

  if (loading) {
    return <div className="loader">Loading orders...</div>;
  }

  return (
    <div>
      <ul>
        <li><a className="active" href="#home">Home</a></li>
        <li><a href="#news">News</a></li>
        <li><a href="#contact">Contact</a></li>
        <li><a href="#about">About</a></li>
        <input
          type="text"
          placeholder="Search by customer name..."
          name="search"
          className="search"
          value={searchQuery}
          onChange={handleSearchChange}
        />
        <button type="submit"><i className="fa fa-search"></i></button>
      </ul>

      <center><h1>Admin Order Management</h1></center>

      <center>
        <table>
          <thead>
            <tr>
              <th>Customer Name</th>
              <th>Product</th>
              <th>Quantity</th>
              <th>NIC</th>
              <th>Account Number</th>
              <th>Amount</th>
              <th>Phone Number</th>
              <th>Address</th>
              <th>Email</th>
              <th>Status</th>
              <th>Created At</th>
              <th>Updated At</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredOrders.length === 0 ? (
              <tr>
                <td colSpan="13">No orders available</td>
              </tr>
            ) : (
              filteredOrders.map((order) => (
                <tr key={order._id}>
                  <td>{order.customerName}</td>
                  <td>{order.product}</td>
                  <td>{order.quantity}</td>
                  <td>{order.NIC}</td>
                  <td>{order.AccountNumber}</td>
                  <td>{order.Amount}</td>
                  <td>{order.PhoneNumber}</td>
                  <td>{order.Address}</td>
                  <td>{order.Email}</td>
                  <td style={{ color: getStatusColor(order.status) }}>
                    {order.status}
                  </td>
                  <td>{formatDate(order.createdAt)}</td>
                  <td>{formatDate(order.updatedAt)}</td>
                  <td>
                    <button
                      onClick={() => handleCancelOrder(order._id)}
                      disabled={order.status === 'Shipped' || order.status === 'Completed'}
                      className="canclebutton"
                      title="Cancel this order"
                    >
                      Cancel
                    </button>
                    <button
                      onClick={() => handleUpdateStatus(order._id, 'Shipped')}
                      disabled={order.status === 'Shipped' || order.status === 'Completed'}
                      className="shippedbutton"
                      title="Mark this order as shipped"
                    >
                      Shipped
                    </button>
                    <button
                      onClick={() => handleUpdateStatus(order._id, 'Completed')}
                      disabled={order.status === 'Completed'}
                      className="completedbutton"
                      title="Mark this order as completed"
                    >
                      Completed
                    </button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </center>
    </div>
  );
};

export default AdminOrderTable;
